INSERT INTO `article` VALUES (14, 'python ssh 连接服务器测试', '<pre class=\"brush:python;toolbar:false\">import&nbsp;paramiko\r\n\r\n#创建SSH对象\r\nssh&nbsp;=&nbsp;paramiko.SSHClient()\r\n\r\n#把要连接的机器添加到known_hosts文件中\r\nssh.set_missing_host_key_policy(paramiko.AutoAddPolicy())\r\n\r\n#连接服务器\r\nssh.connect(hostname=&#39;123.207.22.221&#39;,&nbsp;port=22,&nbsp;username=&#39;root&#39;,&nbsp;password=&#39;123456&#39;)\r\n\r\ncmd&nbsp;=&nbsp;&#39;ls&#39;\r\n#cmd&nbsp;=&nbsp;&#39;ls&nbsp;-l;ifconfig&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#多个命令用;隔开\r\nstdin,&nbsp;stdout,&nbsp;stderr&nbsp;=&nbsp;ssh.exec_command(cmd)\r\n\r\nresult&nbsp;=&nbsp;stdout.read()\r\n\r\nif&nbsp;not&nbsp;result:\r\n&nbsp;&nbsp;&nbsp;&nbsp;result&nbsp;=&nbsp;stderr.read()\r\nssh.close()\r\n\r\nprint(result.decode())</pre><p>原文：https://www.cnblogs.com/whatisfantasy/p/6138663.html</p>', '2018-04-23 07:43:24', '2018-04-23 07:43:24', NULL, 15);
